groups = Group.create([
  { title: "Digimon Fan Club", moderator_id: 1, description: "We are a bunch of Digimon fans. The greatest fighting monster franchise to ever come from Japan! Hang out with us if you love Digimon. We are a relaxed and friendly group of people always looking for new friends. Even if you aren't the biggest fan, stop on by ", city: "Jersey City", state: "NJ", lat: 40.72815759999999, lng: -74.0776417 },
  { title: "ESP Enthusiasts", moderator_id: 4, description: "Practice your ESP with us.", city: "Philadelphia", state: "PA", lat: 39.9525839, lng: -75.1652215 },
  { title: "Splash", moderator_id: 5, description: "If you love splashing in the water then Splash is for you. We also like to make exciting things happen around our city. We are the hottest Meetup in town right now! Stay cool with Splash.", city: "New York", state: "NY", lat: 40.7127837, lng: -74.0059413 },
  { title: "Mimes of Hoboken", moderator_id: 6, description: "In recent years, mimes have become somewhat of a novelty. Merely a French quirk of a byegone era. However, us mimes are a proud bunch, we keep our mime tradition alive. If you are a mime or aspiring mime, come out and hang with us! We are usually in Hoboken, but sometimes take trips to Jersey City and Manhattan to meet with other groups of mimes in the area.", city: "Hoboken", state: "NJ", lat: 40.7439905, lng: -74.0323626 },
  { title: "Fennec Fox Fans", moderator_id: 2, description: "Fennec Foxes are the CUTEST!!! We love fennec foxes. Did you know a fennec fox can jump straight up as high as 61 centimeters, even though they are only 20 centimeters tall?! They can also jump distances of 120 centimeters. What cool animals.", city: "Newark", state: "NJ", lat: 40.735657, lng: -74.1723667 },
  { title: "Citizens Against Breakfast", moderator_id: 7, description: "We are a group of concerned citizens about the widespread practice of consuming breakfast. Often pitched as 'the most important meal of the day', breakfast foods consist of foods high in bad cholesterol and sugar. Pancakes? Merely vessels to consume butter and syrup. Eggs and Bacon? Eat only eggs and bacon for a year and see if you survive. Breakfast is bad.", city: "Newport", state: "NJ", lat: 39.2968298, lng: -75.1769161},
  { title: "Amateur Curlers", moderator_id: 4, description: "We are people that like to go curling! Unfortunately, curling's popularity has gone down in recent years and it can be hard to find friends who will consistently play. If you are a diehard curling fan this group is for you!", city: "Greenville", state: "NJ", lat: 40.6987935, lng: -74.0916944 },
  { title: "Lemur Lovers", moderator_id: 1, description: "Lemurs are great. They do some cool stuff. You should join this group. We meet often and are very passionate about these majestic creatures.", city: "Farmingdale", state: "NY", lat: 40.7325997, lng: -73.4454009 },
  { title: "Stamp Collectors", moderator_id: 6, description: "We are the largest network of stamp collectors in Amityville area. We get together to swamp stamps and show off our coolest ones. If you are new to stamp collecting, or looking to get back into your old hobby, then this group is for you!", city: "Amityville", state: "NY", lat: 40.6789893, lng: -73.4170673 }
])
Group.all.each_with_index do |group, idx|
